import hashes
import pack
import check
import bit_handle

seed = []
message = ""
message = message[::-1]
map = {'1': 1, '0': 0}
for char in message:
    seed += [map.get(char)]
print(check.show(pack.bi2by(seed)))
s = seed.copy()
s.reverse()
s2 = int.from_bytes(bit_handle.arr_to_str(s)[::-1],"big")
print("Day la: %X" %s2)
ciph = hashes.sha3_256(seed)
print(check.show(pack.bi2by(ciph)))
